create table travelimage
(
    ImageID        int auto_increment,
    Title          varchar(255)                   null,
    Description    longtext                       null,
    Latitude       double                         null,
    Longitude      double                         null,
    CityCode       int                            null,
    CountryCodeISO varchar(2)                     null,
    UID            int                            null,
    PATH           varchar(225)                   null,
    Content        varchar(255)                   null,
    FAVOR          int          default 0         null,
    Theme          varchar(255) default 'Scenery' null,
    CityName       varchar(255) default ''        null,
    CountryName    varchar(255) default ''        null,
    constraint travelimage_ImageID_uindex
        unique (ImageID)
)
    charset = utf8;

create index CityCode
    on travelimage (CityCode);

create index CountryCodeISO
    on travelimage (CountryCodeISO);

alter table travelimage
    add primary key (ImageID);

